(1, 'Find the value of: \\[ \\ \\frac{2}{9} + \\frac{3}{27} \\]', '$\\frac{1}{3}$', '$\\frac{1}{27}$', '$\\frac{5}{27}$', NULL, 'choice_a', 'module', 'Mathematics', 1, 'Fractions', 0, '\\[ \\begin{aligned}   \\frac{2}{9} + \\frac{3}{27} \\\\  = \\frac{2 \\times 3 + 3}{27} \\\\  = \\frac{9}{27} \\\\  = \\frac{1}{3}  \\end{aligned} \\]', '2014-10-16 11:00:43', '2014-10-16 11:37:58', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(2, 'Find the value of y: \\[ \\ \\frac{4}{7} - y = \\frac{5}{21} \\]', '$\\frac{1}{7}$', '$\\frac{1}{3}$', '$\\frac{1}{14}$', NULL, 'choice_b', 'module', 'Mathematics', 2, 'Fractions', 0, '\\[ \\begin{aligned} \\frac{4}{7} -y  = \\frac{5}{21} \\\\ \\frac{4}{7} - \\frac{5}{21}  = y \\\\ \\frac{4 \\times 3 - 5}{21}  = y \\\\ \\frac{7}{21}  = y \\\\ \\frac{1}{3}  = y \\end{aligned}  \\]', '2014-10-16 11:12:58', '2014-10-16 11:39:34', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(3, 'Find the value of: \\[ \\ \\frac{2}{7} \\div 1\\frac{1}{2} \\]', '$\\frac{2}{21}$', '$\\frac{4}{21}$', '$\\frac{3}{21}$', NULL, 'choice_b', 'module', 'Mathematics', 3, 'Fractions', 0, '\\[ \\begin{aligned}  = \\frac{2}{7} \\div \\frac{3}{2} \\\\  = \\frac{2}{7} \\times \\frac{2}{3} \\\\ = \\frac{2 \\times 2}{7 \\times  3} \\\\ = \\frac{4}{21} \\\\ \\end{aligned} \\]', '2014-10-16 11:14:34', '2014-10-16 11:40:51', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(4, 'Find the value of x such that: \\[ \\ x \\times \\frac{72}{104} = \\frac{6}{13} \\]', '$\\frac{1}{9}$', '$\\frac{12}{9}$', '$\\frac{6}{9}$', NULL, 'choice_c', 'module', 'Mathematics', 4, 'Fractions', 0, '\\[ \\begin{aligned}  x \\times \\frac{72}{104}  = \\frac{6}{13} \\\\ x  = \\frac{104}{72} \\times \\frac{6}{13} \\\\ \\\\ x  = \\frac{8}{12} \\times \\frac{1}{1} \\\\ x = \\frac{8}{12} \\\\ x  = \\frac{2}{3} \\\\ x  = \\frac{6}{9} \\\\ \\end{aligned} \\]', '2014-10-16 11:15:47', '2014-10-16 11:46:53', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(5, 'The price of gasoline has dropped $\\frac{2}{9}^{th}$ since the last month. If the price last month was $\\$180$ per barrel, the current price per barrel is $\\$140$.', '$TRUE$', '$FALSE$', NULL, NULL, 'choice_a', 'module', 'Mathematics', 5, 'Fractions', 0, '\\[ \\begin{aligned}  Current\\ Price = 180 - \\frac{2}{9} \\times 180 \\\\ = 180 - 40\\\\ = $140 \\end{aligned} \\]', '2014-10-16 11:17:00', '2014-10-16 11:50:00', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(6, 'Compared to last year, we had $\\frac{7}{12}$ times more rain this year. If 156mm fell last year, then we had 85mm more rain this year.', '$True$', '$False$', NULL, NULL, 'choice_b', 'module', 'Mathematics', 6, 'Fractions', 0, '\\[ \\begin{aligned}  More\\ rain(this\\ year) = \\frac{7}{12} \\times 156 \\\\ = 91mm \\end{aligned} \\]', '2014-10-16 11:18:23', '2014-10-16 11:48:54', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(7, 'Find the value of: \\[ \\ (64)^\\frac{2}{3} \\]', '$4096$', '$16$', '$48$', NULL, 'choice_b', 'module', 'Mathematics', 1, 'Indices', 0, '\\[\\begin{aligned}  = ((2)^6)^{\\frac{2}{3}} \\\\ = (2)^{6 \\times \\frac{2}{3}} \\\\ = 2^{12} = 4096 \\end{aligned} \\]', '2014-10-16 12:03:03', '2014-10-16 12:24:09', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(8, 'Find the value of:  \\[ \\ 5^{-3} \\]', '$\\frac{1}{125}$', '$\\frac{1}{5}$', '$-125$', NULL, 'choice_a', 'module', 'Mathematics', 2, 'Indices', 0, '\\[\\begin{aligned} = 5^{-3} \\\\ = \\frac{1}{5^3}\\\\ = \\frac{1}{125}\\\\ \\end{aligned} \\]', '2014-10-16 12:04:54', '2014-10-16 12:23:52', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(9, 'Find the value of: \\[ \\ 144^\\frac{1}{2} \\]', '$20736$', '$72$', '$12$', NULL, 'choice_c', 'module', 'Mathematics', 3, 'Indices', 0, '\\[\\begin{aligned} = \\sqrt{144}\\\\ = 12 \\end{aligned} \\]', '2014-10-16 12:06:13', '2014-10-16 12:23:34', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(10, 'Find the value of: \\[ \\ 36^\\frac{-1}{2} \\]', '$-6$', '$\\frac{-1}{32}$', '$\\frac{1}{6}$', NULL, 'choice_c', 'module', 'Mathematics', 4, 'Indices', 0, '\\[\\begin{aligned} = (6^2)^{\\frac{-1}{2}}\\\\  = 6^{-1} \\\\ = \\frac{1}{6} \\end{aligned} \\]', '2014-10-16 12:07:49', '2014-10-16 12:23:16', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(11, 'Simplify:  \\[ \\ 3xy^2 - 2x^2y + 6x^2y - 8xy^2 \\]', '$x^2y - 2xy^2$', '$-x^2y^2$', '$4x^2y - 5xy^2$', NULL, 'choice_c', 'module', 'Mathematics', 1, 'Algebra 1', 0, '\\[\\begin{aligned}  = 3xy^2 - 8xy^2 + 6x^2y - 2x^2y \\\\ = 4x^2y - 5xy^2  \\end{aligned} \\]', '2014-10-16 14:45:02', '2014-10-16 14:45:02', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(12, 'Using $x=6.2$, $y=-3.2$ and $z = \\frac{2}{3}$. Find the value $($to three significant figures$)$: \\[ \\ x^2z - y \\]', '$2.88$', '$28.8$', '$288$', NULL, 'choice_b', 'module', 'Mathematics', 2, 'Algebra 1', 0, '\\[\\begin{aligned}  \\ x^2z - y \\\\= 6.2^2 \\times \\frac{2}{3} - (-3.2)\\\\ = 38.44 \\times \\frac{2}{3} + 3.2\\\\ = 25.6 + 3.2\\\\ = 28.8 \\end{aligned} \\]', '2014-10-16 14:46:48', '2014-10-16 14:52:19', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(13, 'Using $x=6.2$, $y=-3.2$ and $z = \\frac{2}{3}$. Find the value $($to three significant figures$)$: \\[ y^2 - \\frac{x}{z}  \\]', '$0.94$', '$9.4$', '$94$', NULL, 'choice_a', 'module', 'Mathematics', 3, 'Algebra 1', 0, '\\[\\begin{aligned}  y^2 - \\frac{x}{z}\\\\ = (-3.2)^2 - 6.2 \\times \\frac{3}{2}\\\\  = 10.24 - 9.3\\\\  = 0.94  \\end{aligned} \\]', '2014-10-16 14:47:59', '2014-10-16 14:53:40', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(14, 'Using $x=6.2$, $y=-3.2$ and $z = \\frac{2}{3}$. Find the value $($to three significant figures$)$: \\[ \\ (xz -y)^2 \\]', '$53.8$', '$5.38$', '$0.538$', NULL, 'choice_a', 'module', 'Mathematics', 4, 'Algebra 1', 0, '\\[\\begin{aligned} \\ (xz -y)^2 \\\\ = (6.2 \\times \\frac{2}{3} - (-3.2))^2 \\\\  = (4.13 + 3.2)^2\\\\  = 53.8  \\end{aligned} \\]', '2014-10-16 14:49:19', '2014-10-16 14:54:33', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(15, 'Simplify:  \\[ \\ (y-4)(y-3) \\]', '$y^2-7y+12$', '$y^2 -12$', '$y^2+12$', NULL, 'choice_a', 'module', 'Mathematics', 1, 'Algebra 2', 0, '\\[\\begin{aligned} \\ (y-4)(y-3) \\\\ = y^2 - 4y - 3y + 12\\\\ = y^2 -7y + 12  \\end{aligned} \\]', '2014-10-16 14:57:25', '2014-10-16 15:07:51', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(16, 'Simplify:  \\[ \\ (a+2)^2 \\]', '$a^2 + 4$', '$a^2 + 4a + 4$', '$a^2 + 4a^2$', NULL, 'choice_b', 'module', 'Mathematics', 2, 'Algebra 2', 0, 'Using the formula:  \\[ (a + b)^2 = a^2 + b^2 + 2ab \\]  we get:  \\[\\begin{aligned}  \\ (a+2)^2 \\\\  (a + 2) = a^2 + 4 + 4a   \\end{aligned} \\]', '2014-10-16 15:00:13', '2014-10-16 15:07:38', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(17, 'Factorise:  \\[ \\ x^2 + 5x + 6 \\]', '$(x^2 + 5x)\\frac{6}{5}$', '$(x+5)(x+6)$', '$(x+2)(x+3)$', NULL, 'choice_c', 'module', 'Mathematics', 3, 'Algebra 2', 0, '"\\[\\begin{aligned}  x^2 + 5x + 6\\\\  = x^2 + 3x + 2x + 6\\\\  = x(x + 3) + 2(x + 3)\\\\  = (x + 2)(x + 3)  \\end{aligned} \\]', '2014-10-16 15:02:32', '2014-10-16 15:07:18', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(18, 'Factorise:  \\[ \\ 12x^2-6x \\]', '$6x(2x-1)$', '$(x-6)(x+6)$', '$(x^2-6)(x+2)$', NULL, 'choice_a', 'module', 'Mathematics', 4, 'Algebra 2', 0, '\\[\\begin{aligned}  12x^2 - 6x\\\\  = 6x(2x - 1)  \\end{aligned} \\]', '2014-10-16 15:03:54', '2014-10-16 15:06:50', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(19, 'Make $u$ the subject of the formula:  \\[ \\ v^2 = u^2 + 2as \\]', '$u = \\sqrt{(v^2 - 2as)}$', '$u = \\sqrt{(v - 2as)}$', '$u = \\sqrt{(v^2 - 2)as}$', NULL, 'choice_a', 'module', 'Mathematics', 5, 'Algebra 2', 0, '\\[\\begin{aligned}  v^2  - 2as = u^2\\\\ u = \\sqrt{(v^2 - 2as)} \\end{aligned} \\]', '2014-10-16 15:05:03', '2014-10-16 15:07:02', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(20, 'Solve:  \\[ \\ x^2 -5x + 6 =0 \\]', '$x=2,\\ x=3$', '$x=2,\\ x=1$', '$x=5,\\ x=2$', NULL, 'choice_a', 'module', 'Mathematics', 1, 'Equations 1', 0, '\\[\\begin{aligned} \\ x^2 -5x + 6 =0 \\\\  x^2 -3x - 2x + 6  = 0\\\\ x(x -3) -2 (x - 3) = 0\\\\ (x - 2)(x-3) = 0\\\\ x=2 x = 3 \\end{aligned} \\]', '2014-10-16 15:10:58', '2014-10-16 15:16:58', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(21, 'Solve:  \\[  \\ x^2 - 4x - 5=0 \\]', '$x=1,\\ x = 5/4$', '$x=-1,\\ x = 5$', '$x=1,\\ x = -5$', NULL, 'choice_b', 'module', 'Mathematics', 2, 'Equations 1', 0, '\\[\\begin{aligned}   x^2 - 4x - 5=0 \\\\ x^2 -5x + x -5   = 0\\\\ x(x - 5) + 1(x - 5)  = 0\\\\ (x + 1)(x - 5) = 0\\\\ x = -1, x  = 5  \\end{aligned} \\]', '2014-10-16 15:12:52', '2014-10-16 15:16:41', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(22, 'Solve:  \\[ \\ 4^{2x-1} = 32 \\]', '$x = \\sqrt{8}$', '$x = 8$', '$x = 1\\frac{3}{4}$', NULL, 'choice_c', 'module', 'Mathematics', 3, 'Equations 1', 0, '\\[\\begin{aligned}  \\ 4^{2x-1} = 32 \\\\2^{2 \\times (2x - 1)}  = 2^5\\\\ 2 \\times (2x - 1)  = 5\\\\ 4x - 2 = 5\\\\ x = \\frac{7}{4}\\\\ x = 1\\frac{3}{4}  \\end{aligned} \\]', '2014-10-16 15:14:34', '2014-10-16 15:16:28', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(23, 'Solve: \\[ \\ 3x^2 - 7x + 2 = 0 \\]  by factorization.', '$x=\\frac{2}{7},\\ x=3$', '$x=\\frac{1}{3},\\ x=2$', '$x=\\frac{2}{7},\\ x=7$', NULL, 'choice_b', 'module', 'Mathematics', 1, 'More Algebra and Equations', 0, '\\[\\begin{aligned}  3x^2 - 7x + 2 = 0\\\\ 3x^2 - 6x - x + 2  = 0\\\\ 3x(x - 2) -1(x - 2)  = 0\\\\ (3x - 1)(x - 2)\\\\ x = 2, x  = \\frac{1}{3}  \\end{aligned} \\]', '2014-10-16 15:19:56', '2014-10-16 17:43:23', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(24, 'Solve: \\[ \\ 2x^2 + 5x + 2 = 0 \\]  by using the quadratic formula.', '$x=-\\frac{1}{2},\\ x=-2$', '$x=-\\frac{2}{5},\\ x=-2$', '$x=-\\frac{2}{5},\\ x=-1$', NULL, 'choice_a', 'module', 'Mathematics', 2, 'More Algebra and Equations', 0, '\\[\\begin{aligned}  2x^2 + 5x + 2 = 0\\\\ 2x^2 + 4x + x + 2 = 0\\\\ 2x(x + 2) + 1(x + 2)  = 0\\\\ (2x + 1)(x + 2)  = 0\\\\ x =-2, x = -\\frac{1}{2}  \\end{aligned} \\]', '2014-10-16 15:21:19', '2014-10-16 17:43:11', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(25, 'Solve: \\[ \\ 4x^2 - 7x - 2 =0 \\]  by completing the square.', '$x=-\\frac{2}{7},\\ x=-2$', '$x=-\\frac{1}{4},\\ x=2$', '$x=-\\frac{7}{4},\\ x=-\\frac{1}{2}$', NULL, 'choice_b', 'module', 'Mathematics', 3, 'More Algebra and Equations', 0, '\\[\\begin{aligned} 4x^2 - 7x - 2 =0\\\\4x^2 -7x + (\\frac{7}{4})^2 - 2 - (\\frac{7}{4})^2 = 0\\\\(2x - \\frac{7}{4})^2 = 2 + \\frac{49}{16}\\\\(2x - \\frac{7}{4})^2 = \\frac{81}{16}\\\\ 2x - \\frac{7}{4} = \\pm \\frac{9}{4} \\\\x = 2\\ or\\ x = -\\frac{1}{4} \\end{aligned} \\]', '2014-10-16 15:25:36', '2014-10-16 17:42:59', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(26, 'Make $r$ the subject of the formula: \\[ \\ p = \\frac{r-y}{r+s} \\]', '$r = \\frac{y + ps}{p + 1}$', '$r = \\frac{(y + ps)}{(p - 1)}$', '$r = \\frac{-(y + ps)}{(p - 1)}$', NULL, 'choice_c', 'module', 'Mathematics', 4, 'More Algebra and Equations', 0, '\\[\\begin{aligned}  \\ p = \\frac{r-y}{r+s} \\\\pr + ps  = r - y\\\\ pr -r = -ps - y\\\\ r = \\frac{-(y + ps)}{(p - 1)}  \\end{aligned} \\]', '2014-10-16 15:36:41', '2014-10-16 17:42:46', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(27, 'Simplify:  \\[ \\ \\frac{8(a^2 - b^2)}{3(a + 2)} \\div \\frac{4(a - b)}{27(a + 1)} \\]', '$\\frac{18(a + b)(a + 1)}{(a + 2)}$', '$\\frac{(a + b)(a + 1)}{(a + 2)}$', '$\\frac{9(a + b)(a + 1)}{2(a + 2)}$', NULL, 'choice_a', 'module', 'Mathematics', 5, 'More Algebra and Equations', 0, '\\[\\begin{aligned} \\frac{8(a^2 - b^2)}{3(a + 2)} \\div \\frac{4(a - b)}{27(a + 1)} \\\\ =\\frac{8(a^2 - b^2)}{3(a+2)} \\times \\frac{27(a+1)}{4(a-b)}\\\\ =\\frac{8(a-b)(a+b)}{3(a+2)} \\times \\frac{27(a+1)}{4(a-b)}\\\\ =\\frac{2(a + b)}{(a+2)} \\times 9(a+1)\\\\ =\\frac{18(a + b)(a+1)}{(a+2)} \\end{aligned} \\]', '2014-10-16 15:41:18', '2014-10-16 17:42:33', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(28, 'Rajesh decided to decorate his bedroom. The total area of the walls was $48m^2$. If one roll of wallpaper covers $5m^2$ of wall, how many rolls of wallpaper did Rajesh need?', '$13 rolls$', '$10 rolls$', '$11 rolls$', NULL, 'choice_b', 'module', 'Mathematics', 4, 'Approximating & Checking Calculations', 0, '\\[\\begin{aligned} Total\\ area = 48m^2\\\\ Total\\ Sheets\\ required = \\frac{48}{5}\\\\ Nearest\\ Integer = 10\\\\ \\end{aligned} \\]', '2014-10-16 22:39:17', '2014-10-17 12:37:23', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
