(39, 'Find the value of:  \\[ \\ \\frac{5(25 - 10)}{9} \\]', '$833.333333333333333$ or $833.\\dot{3}$', '$83.333333333333333$ or $83.\\dot{3}$', '$8.3333333333333333$ or $8.\\dot{3}$', NULL, 'choice_c', 'module', 'Mathematics', 3, 'Using A Calculator', 0, '\\[\\begin{aligned} \\frac{5(25 - 10)}{9} \\\\ = \\frac{5 \\times 15)}{9}\\\\ = \\frac{75}{9}\\\\ = 8.\\dot{3} \\end{aligned} \\]', '2014-10-16 23:17:37', '2014-10-16 23:18:37', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(40, 'What''s the fractional equivalent of $0.\\dot{1}\\dot{5}$ ?', '$\\frac{5}{99}$', '$\\frac{5}{33}$', '$\\frac{15}{90}$', NULL, 'choice_b', 'module', 'Mathematics', 1, 'Recurring Decimals & Surds', 0, '\\[\\begin{aligned} 0.\\dot{1}\\dot{5} \\\\ x = 0.15 \\ldots \\\\ 100x = 15.15 \\ldots \\\\ 99x  = 15 \\\\ x = \\frac{15}{99} \\end{aligned} \\]', '2014-10-16 23:21:44', '2014-10-16 23:23:24', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(41, 'What is the decimal equivalent of:  \\[ \\frac{281}{990} \\]', '$0.2\\dot{8}\\dot{3}$', '$0.28\\dot{3}$', '$0.2\\dot{8}\\dot{1}$', NULL, 'choice_a', 'module', 'Mathematics', 2, 'Recurring Decimals & Surds', 0, '\\[\\begin{aligned} \\frac{281}{990} \\\\ x = \\frac{281}{990} \\\\ 1000x - 10x = 281 \\end{aligned} \\]\r\n\r\nOnly x = $0.28383 \\ldots = 0.2\\dot{8}\\dot{3}$ satisfies the above equation. We can arrive at this by simple division too.', '2014-10-16 23:25:23', '2014-10-16 23:26:16', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(42, 'Simplify:  \\[ \\ (\\sqrt(2) - 3)^2 \\]', '$11 - 6\\sqrt{2}$', '$11 - 6$', '$11 - \\sqrt{2}$', NULL, 'choice_a', 'module', 'Mathematics', 3, 'Recurring Decimals & Surds', 0, '\\[ \\ (\\sqrt(2) - 3)^2 \\] \r\nUsing the formula:  \\[ (a - b)^2 = a^2 + b^2 - 2ab \\]\r\n\r\nWe get:\r\n\r\n\\[\\begin{aligned} =\\sqrt{2}^2 + 3^2 - 2 \\times \\sqrt{2} \\times 3 \\\\  = 11 - 6\\sqrt{2} \\end{aligned} \\]', '2014-10-16 23:28:44', '2014-10-16 23:29:35', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(43, 'Simplify:  \\[ \\   6(\\sqrt{2}+3) - \\sqrt{2}(2 + \\sqrt{3}) \\]', '$4\\sqrt{2} - \\sqrt{6}$', '$4\\sqrt{2} - \\sqrt{6} + 18$', '$4\\sqrt{2} - \\sqrt{6} -18$', NULL, 'choice_b', 'module', 'Mathematics', 4, 'Recurring Decimals & Surds', 0, '\\[\\begin{aligned} \\   6(\\sqrt{2}+3) - \\sqrt{2}(2 + \\sqrt{3}) \\\\ = 6\\sqrt{2} + 18 - 2\\sqrt{2} - \\sqrt{6}\\\\  = 4\\sqrt{2} -\\sqrt{6} + 18 \\end{aligned} \\]', '2014-10-16 23:31:01', '2014-10-16 23:31:01', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(44, 'Simplify:  \\[ \\ \\frac{1}{\\sqrt{3}} \\]', '$\\frac{\\sqrt{3}}{1}$', '$\\sqrt{3}$', '$\\frac{\\sqrt{3}}{3}$', NULL, 'choice_c', 'module', 'Mathematics', 5, 'Recurring Decimals & Surds', 0, '\\[ \\ \\frac{1}{\\sqrt{3}} \\]\r\nOn multiplying and dividing by $\\sqrt{3}$ we get\r\n\r\n\\[\\begin{aligned} = \\frac{1 \\times \\sqrt{3}}{\\sqrt{3} \\times \\sqrt{3}}\\\\ = \\frac{\\sqrt{3}}{3}\\\\ \\end{aligned} \\]', '2014-10-16 23:33:21', '2014-10-16 23:34:11', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(45, '$96$ is the _________ of $12$ & $32$.', 'LCM', 'HCF', 'Product', 'Prime factor', 'choice_a', 'module', 'Mathematics', 1, 'Types of Numbers', 0, '\\[\\begin{aligned} 12 = 2^2 \\times 3 \\\\ 32 = 2^5 \\\\ LCM(12, 32) = 2^5 \\times 3\\\\ LCM(12, 32) = 96 \\end{aligned} \\]', '2014-10-17 00:36:15', '2014-10-17 00:45:10', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
