(89, 'If h = ab, a = 2.7 (1 d.p.) and b = 20 (nearest whole number), what is the lower bound of h?', '$51.4$', '$52.57$', '$51.675$', NULL, 'choice_c', 'module', 'Mathematics', 4, 'Upper & Lower Bounds of Measurement', 0, '\\[ \\begin{aligned}\r\n 2.7 (1 decimal\\ place ) = 2.7 \\pm 0.5 \\\\\r\n 20 (nearest\\ whole\\ number)  = 20 \\pm 0.5 \\\\\r\n Lower\\ bound  = 19.5 \\times 2.65 = 51.675 \r\n\\end{aligned} \\]', '2014-10-17 13:03:02', '2014-10-17 13:03:02', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(90, 'Simplify \\[ 5a + 2a - (-3a) \\]', '$10 + a$', '$10a$', '$4a$', NULL, 'choice_b', 'module', 'Mathematics', 5, 'Algebra 1', 0, '\\[\r\n\\ 5a + 2a - (-3a) = 7a + 3a = 10a\r\n\\]', '2014-10-17 13:05:06', '2014-10-17 13:05:06', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(91, 'Simplify: \\[5x - 2y - 3x + 6y + 7x \\]', '$9x + 4y$', '$13(x + y)$', '$13xy$', NULL, 'choice_a', 'module', 'Mathematics', 6, 'Algebra 1', 0, '\\[\r\n\\ 5x-2y-3x+6y+7x\\\\ = 5x-3x+7x-2y+6y \\\\= 9x+4y \r\n\\]', '2014-10-17 13:05:51', '2014-10-17 13:06:37', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(92, 'Solve: \\[6x - 1 = 15 + 2x\\]', '$x = 4$', '$x = 7/2$', '$x = 2$', NULL, 'choice_a', 'module', 'Mathematics', 4, 'Equations 1', 0, '\\[ \\begin{aligned}\r\n 6x - 1 = 15+2x \\\\\r\n 4x = 16 \\\\\r\nx = \\frac{16}{4} \\\\\r\n x = 4 \r\n\\end{aligned} \\]', '2014-10-17 13:09:19', '2014-10-17 13:11:09', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(93, 'Solve: \\[2(x - 1) = 6(2x + 2)\\]', '$1$', '$-3$', '$-1.4$', NULL, 'choice_c', 'module', 'Mathematics', 5, 'Equations 1', 0, '\\[ \\begin{aligned} \r\n 2(x - 1) = 6(2x + 2) \\\\\r\n 2x -2 = 12x + 12 \\\\ \r\n 10x = -14 \\\\\r\nx = \\frac{-14}{10}\\\\\r\n x = -1.4\r\n\\end{aligned} \\]', '2014-10-17 13:10:39', '2014-10-17 13:10:39', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(94, 'Solve:\\[ 4x + 7y = 10 \\\\ 2x + 3y = 3 \\]', '$x = -4.5, y = 4$', '$x = 4.5, y = 4$', '$x = 4.5, y = 5$', NULL, 'choice_a', 'module', 'Mathematics', 3, 'Equations 2', 0, '\\[ \\begin{aligned}\r\n 4x + 7y  = 10\\\\\r\n 2x + 3y  = 3\\\\ \\\\ \r\n\r\n 4x + 7y = 10\\\\\r\n 4x + 6y = 6\\\\ \\\\ \r\n\r\n y = 4\r\n\r\n\\end{aligned} \\]\r\n\r\nOn substituting the value of y:\r\n\r\n\\[ \\begin{aligned}\r\n 4x + 28 = 10 \\\\\r\n 4x = -18 \\\\\r\n x = -4.5\r\n\\end{aligned} \\]', '2014-10-17 13:13:35', '2014-10-17 13:16:29', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(95, 'Solve \\[3a - 5b = 1 \\\\ 2a + 3b = 7\\]', '$a = 1, b = 2$', '$a = 2, b = 2$', '$a = 2, b = 1$', NULL, 'choice_c', 'module', 'Mathematics', 4, 'Equations 2', 0, '\\[ \\begin{aligned} \r\n\r\n  3a - 5b  = 1 \\\\\r\n  2a + 3b  = 7 \\\\ \\\\\r\n\r\n 3 \\times (3a - 5b = 1) \\\\\r\n 5 \\times (2a + 3b = 7) \\\\ \\\\ \r\n\r\n 9a - 15b  = 3 \\\\\r\n 10a + 15b  = 38 \\\\ \\\\\r\n\r\n19a  = 38\\\\\r\na  = 2\r\n\r\n\\end{aligned} \\]\r\n\r\nOn substituting the value of $a$:\r\n\r\n\\[ \\begin{aligned} \r\n  3a - 5b = 1 \\\\\r\n  6 -5b = 1 \\\\\r\n  5 = 5b \\\\\r\n  b = 1\r\n\r\n\\end{aligned} \\]', '2014-10-17 13:17:52', '2014-10-17 13:20:32', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(96, 'Solve: \\[\\ 2a - 3b = 12 \\\\ \\ 4a + 3b = 6 \\]', '$a = 3, b = 2$', '$a = 3, b = -2 $', '$a = -3, b = -2$', NULL, 'choice_b', 'module', 'Mathematics', 5, 'Equations 2', 0, '\\[ \\begin{aligned} \r\n\r\n  2a-3b = 12 \\\\\r\n  4a+3b = 6 \\\\ \\\\\r\n\r\n  6a = 18 \\\\\r\n  a  = 3 \r\n\r\n\\end{aligned} \\]\r\n\r\nOn substituting the value of $a$:\r\n\r\n\\[ \\begin{aligned}\r\n  2a-3b  = 12 \\\\\r\n  6 - 3b = 12 \\\\\r\n  3b = -6 \\\\\r\n   b = -2\r\n\\end{aligned} \\]', '2014-10-17 13:19:09', '2014-10-17 13:19:09', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(97, 'What is the gradient for the straight line graph:\\[ y = 4 + 2x \\]', '$4$', '$2$', '$3$', NULL, 'choice_b', 'module', 'Mathematics', 1, 'Straight Line Graphs', 0, '\\[ y = 4 + 2x \\]\r\n\r\nOn comparing the equation with the slope intercept form:\r\n\\[\r\n\\ y = mx + c\r\n\\]\r\n\r\nWhere $m$ is the slope, we get $m=2$.', '2014-10-17 13:32:58', '2014-10-17 13:32:58', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(98, 'What is the intercept for the graph: \\[ 2y = 6x + 4 \\] ', '$(0, 2)$', '$(0, 4)$', '$(2, 2)$', NULL, 'choice_a', 'module', 'Mathematics', 2, 'Straight Line Graphs', 0, '\\[ 2y = 6x + 4 \\] \r\nThe first step is to write this equation as per teh slope intercept form:\r\n\\[ \r\n\\ y = mx + c \r\n\\]\r\n\r\nAfter which we get, \r\n\r\n\\[\r\n\\ y = 3x + 2\r\n\\]\r\n\r\nWhere $c$ is the intercept with the y-axis. Thus $c=2$ and the intercept is $(0,2)$', '2014-10-17 13:34:11', '2014-10-17 13:36:22', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
(99, 'What is the intercept for the graph: \\[ y = 3x - 2\\]', '$(0, -2)$', '$(0, -4)$', '$(2, 2)$', NULL, 'choice_a', 'module', 'Mathematics', 3, 'Straight Line Graphs', 0, '\\[ y = 3x - 2\\]\r\n\r\nThe first step is to write this equation as per teh slope intercept form:\r\n\r\n\\[ \r\n\\ y = mx + c \r\n\\]\r\n\r\nAfter which we get, \r\n\r\n\\[\r\n\\ y = 3x - 2 \r\n\\]\r\n\r\nWhere $c$ is the intercept with the y-axis. Thus $c=-2$ and the intercept is $(0,-2)$', '2014-10-17 13:35:17', '2014-10-17 13:35:17', 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL, 'NULL', 'NULL', 0, NULL),
